rs")){					$("#MastersPar").prop("checked",false);            	}else if(data.includes("Process Masters")){					$("#PMastersPar").prop("checked",false);            	}else if(data.includes("Location Masters")){					$("#LMastersPar").prop("checked",false);            	}else if(data.includes("Agreement")){					$("#AgreementPar").prop("checked",false);            	}            });                   }        	}	function allpage(th,pgs){		if($(th).prop('checked') == true){			$("."+pgs).prop('checked', true);			$("."+pgs).trigger('change');		}else if($(th).prop('checked') == false){			$("."+pgs).prop('checked', false);			$("."+pgs).trigger('change');		}	}</script><?php$sel=mysql_query("SELECT * FROM `user_pages` where `uId`='".$_REQUEST['uid']."' and sts='A'");while ($sels=mysql_fetch_assoc($sel)) {	?>	<script type="text/javascript">		$("<?='#lbchk'.$sels['pid']?>").prop("checked",true);		$("<?='#lbpg'.$sels['pid']?>").css("background-color",'#4CAF50');		$("<?='#lbpg'.$sels['pid']?>").css("color",'#fff');			</script>	<?php}?><?php/* ================================================================================================================	checking checkboxes of selection "all" if all submenus checked code here...........   ================================================================================================================*//*$selM=mysql_query("SELECT up.*, mp.pageParent FROM `user_pages` up, master_pages mp where up.`uId`='".$_REQUEST['uid']."' and up.pid=mp.pid and mp.pageParent='Masters' and up.sts='A'");$selMA=mysql_query("SELECT up.*, mp.pageParent FROM `user_pages` up, master_pages mp where up.`uId`='".$_REQUEST['uid']."' and up.sts='A' and up.pid=mp.pid and mp.pageParent='Masters' and up.sts='A'");if(mysql_num_rows($selM) == mysql_num_rows($selMA) && mysql_num_rows($selM)!=0){	?>	<script type="text/javascript">		$("#MastersPar").prop("checked"