<?php include 'cdns.php'; ?><style type="text/css">	body{		background-color: #ADFF5B;	}</style><center>	<div class="login-form" style="width: 400px;">                    <br>            <br>            <br>            <br>            <br>            <br>            <!-- <h2 style="color:#3f779e;font-family: 'Poppins', sans-serif;font-size: 50px;">Agreement</h2> -->            <br>                          <div id="msgdanger" style="display: none;text-align: left;" class="alert alert-danger" role="alert">                              </div>              <div id="msgsuccess" style="display: none;text-align: left;" class="alert alert-success" role="alert">                              </div>                          <h3>Forgot Password</h3>                               <div class="form-group">                <input type="text" class="frminp form-control " placeholder="Enter Your Registered Mail" id="email" required="required" autofocus>            </div>                        <!-- <div class="form-group">                <select class="frminp form-control" name="year" required="required">                  <option value="2019">2019</option>                  <option value="2020">2020</option>                </select>            </div> -->            <div class="form-group">                <button id="subbtn" type="button" onclick="sendmail()" class=" frminp btn btn-primary btn-block">Submit</button>                <br>                <a href="index.php" style="color:#3F779E;font-weight: bold;">&larr; Go Back To Login Page</a>            </div>            <div class="clearfix" >                                           </div>                 <br>            <br>            <br>            <br>            <br>            <br>            <br>                       </div>	</center><script type="text/javascript">	function sendmail(){		var email = $('#email').val();		$.post("profileAjax.php",{act:'forgetPass',email:email},function(data) {	        console.log(data);	        $('#msgsuccess').html('');			$('#msgsuccess').hide();			$('#msgdanger').html('');			$('#msgdanger').hide();	        if(data.includes("mailsent")){				$('#msgsuccess').html(' <b>Temporary password sent to your Mail</b> <br> Please login with it. ');				$('#msgsuccess').show(500);	       	        }else if(data.includes("mailnotsent")){								$('#msgdanger').html(' <b>Something went wrong!</b> <br> Please try again after sometime. ');				$('#msgdanger').show(500);	        }else if(data.includes("notfound")){	            				$('#msgdanger').html(' <b>This Email not associatied to any account</b> <br> Please enter Email used in this software. ');				$('#msgdanger').show(500); 	        }	    });	}	var input = document.getElementById("email");	input.addEventListener("keyup", function(event) {	  // Number 13 is the "Enter" key on the keyboard	  if (event.keyCode === 13) {	    // Cancel the default action, if needed	    event.preventDefault();	    // Trigger the button element with a click	    document.getElementById("subbtn").click();	  }	});</script>